import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';

const AddBlog = () => {
  const { user } = useAuth();

  const [formData, setFormData] = useState({
    title: '',
    content: '',
    country: '',
    dateOfVisit: '',
    userId: user.id
  });

  const [imageFile, setImageFile] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleImageChange = (e) => {
    setImageFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Create a FormData object to append form data and the image
    const form = new FormData();
    form.append('title', formData.title);
    form.append('content', formData.content);
    form.append('country', formData.country);
    form.append('visit_date', formData.dateOfVisit);
    form.append('author_id', formData.userId);
    form.append('email', user.email);
    if (imageFile) {
      form.append('image', imageFile);
    }

    try {
      const response = await fetch('http://localhost:5000/api/blog/add/&email=${encodeURIComponent(user.email)}', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
        body: form, // Send form data with image
      });

      if (!response.ok) {
        throw new Error('Failed to add blog post.');
      }

      alert('Blog post added!');
    } catch (err) {
      console.error(err);
      alert('Error adding blog post.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-6 bg-white rounded-2xl shadow-md space-y-4">
      <h2 className="text-2xl font-bold text-center mb-4">Add Blog Post</h2>

      <div>
        <label htmlFor="title" className="block text-sm font-medium text-gray-700">Title</label>
        <input
          type="text"
          name="title"
          id="title"
          onChange={handleChange}
          required
          className="mt-1 w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring focus:border-blue-500"
        />
      </div>

      <div>
        <label htmlFor="content" className="block text-sm font-medium text-gray-700">Content</label>
        <textarea
          name="content"
          id="content"
          onChange={handleChange}
          required
          rows="4"
          className="mt-1 w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring focus:border-blue-500"
        />
      </div>

      <div>
        <label htmlFor="country" className="block text-sm font-medium text-gray-700">Country</label>
        <input
          type="text"
          name="country"
          id="country"
          onChange={handleChange}
          required
          className="mt-1 w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring focus:border-blue-500"
        />
      </div>

      <div>
        <label htmlFor="dateOfVisit" className="block text-sm font-medium text-gray-700">Date of Visit</label>
        <input
          type="date"
          name="dateOfVisit"
          id="dateOfVisit"
          onChange={handleChange}
          required
          className="mt-1 w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring focus:border-blue-500"
        />
      </div>

      {/* Image Upload */}
      <div>
        <label htmlFor="image" className="block text-sm font-medium text-gray-700">Add Picture</label>
        <input
          type="file"
          name="image"
          id="image"
          accept="image/*"
          onChange={handleImageChange}
          className="mt-1 w-full"
        />
      </div>

      <button
        type="submit"
        className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition-colors"
      >
        Add Blog Post
      </button>
    </form>
  );
};

export default AddBlog;
