import { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useParams, useNavigate } from 'react-router-dom';
import { format } from 'date-fns';

const AuthorProfile = () => {
  const { user } = useAuth();
  const { authorId } = useParams();
  const navigate = useNavigate();
  const [data, setData] = useState({
    author: null,
    blogs: [],
    isFollowing: false,
    allBlogs: [] // Stores all blogs for sorting
  });
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    postCount: 5,
    sortBy: 'recent'
  });

  useEffect(() => {
    const fetchAuthorData = async () => {
      try {
        setLoading(true);
        
        const response = await fetch(
          `http://localhost:5000/api/author/fetch?authorID=${authorId}&email=${encodeURIComponent(user.email)}`,
          {
            method: 'GET',
            headers: {
              'Authorization': `Bearer ${user.token}`
            }
          }
        );
        
        const result = await response.json();
        setData({
          author: result.author,
          blogs: result.blogs,
          allBlogs: result.blogs,
          isFollowing: result.isFollowing
        });
        
      } catch (error) {
        console.error('Error loading author data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchAuthorData();
  }, [authorId, user.token, user.email]);

  useEffect(() => {
    if (data.allBlogs.length > 0) {
      let sortedBlogs = [...data.allBlogs];
      
      switch (filters.sortBy) {
        case 'recent':
          sortedBlogs.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
          break;
        case 'popular':
          sortedBlogs.sort((a, b) => (b.likes * 1 + b.dislikes * 0.5) - (a.likes * 1 + a.dislikes * 0.5));
          break;
        case 'most_liked':
          sortedBlogs.sort((a, b) => b.likes - a.likes);
          break;
        default:
          break;
      }

      setData(prev => ({
        ...prev,
        blogs: sortedBlogs.slice(0, filters.postCount)
      }));
    }
  }, [filters, data.allBlogs]);

  const handleFollowToggle = async () => {
    try {
      const response = await fetch(
        `http://localhost:5000/api/author/follow?email=${encodeURIComponent(user.email)}`, 
        {
          method: data.isFollowing ? 'DELETE' : 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${user.token}`
          },
          body: JSON.stringify({
            followerId: user.id,
            authorId
          })
        }
      );

      if (response.ok) {
        setData(prev => ({
          ...prev,
          isFollowing: !prev.isFollowing
        }));
      }
    } catch (error) {
      console.error('Error updating follow status:', error);
    }
  };

  const handleReaction = async (blogId, reaction) => {
    try {
      const response = await fetch(
        `http://localhost:5000/api/blog/reaction?blogId=${blogId}&reaction=${reaction}`, 
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${user.token}`
          },
          body: JSON.stringify({
            email: user.email,
          }),
        }
      );

      if (response.ok) {
        setData(prev => {
          const updatedBlogs = prev.allBlogs.map(blog => {
            if (blog.id === blogId) {
              return {
                ...blog,
                userReaction: reaction,
                likes: reaction === 'like' ? blog.likes + 1 : blog.likes,
                dislikes: reaction === 'dislike' ? blog.dislikes + 1 : blog.dislikes,
              };
            }
            return blog;
          });

          return {
            ...prev,
            allBlogs: updatedBlogs,
            blogs: updatedBlogs 
          };
        });
      }
    } catch (error) {
      console.error(`Error ${reaction} blog:`, error);
    }
  };

  if (loading) return <div>Loading author profile...</div>;
  if (!data.author) return <div>Author not found</div>;

  return (
    <div className="author-profile">
      <div className="author-header">
        <h1>{data.author.username}</h1>
        <button 
          onClick={handleFollowToggle}
          className={`follow-btn ${data.isFollowing ? 'following' : ''}`}
        >
          {data.isFollowing ? 'Unfollow' : 'Follow'}
        </button>
      </div>

      <div className="filter-section">
        <select
          value={filters.postCount}
          onChange={(e) => setFilters({...filters, postCount: Number(e.target.value)})}
        >
          {[3, 5, 10, 15, 20].map(num => (
            <option key={num} value={num}>Show {num} posts</option>
          ))}
        </select>

        <select
          value={filters.sortBy}
          onChange={(e) => setFilters({...filters, sortBy: e.target.value})}
        >
          <option value="recent">Most Recent</option>
          <option value="popular">Most Popular</option>
          <option value="most_liked">Most Liked</option>
        </select>
      </div>

      <div className="author-blogs">
        {data.blogs.length === 0 ? (
          <p>No blogs found for this author</p>
        ) : (
          data.blogs.map(blog => (
            <div key={blog.id} className="blog-card">
              <h3>{blog.title}</h3>
              <div className="blog-meta">
                <span>üìç {blog.country}</span>
                <span>üóì {format(new Date(blog.visit_date), 'MMMM yyyy')}</span>
              </div>
              <p>{blog.content.substring(0, 200)}...</p>
              
              <div className="reaction-buttons">
                <button
                  onClick={() => handleReaction(blog.id, 'like')}
                  className={blog.userReaction === 'like' ? 'active' : ''}
                >
                  üëç {blog.likes || 0}
                </button>
                <button
                  onClick={() => handleReaction(blog.id, 'dislike')}
                  className={blog.userReaction === 'dislike' ? 'active' : ''}
                >
                  üëé {blog.dislikes || 0}
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default AuthorProfile;
