import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';  // Import AuthProvider
import ProtectedRoute from './components/ProtectedRoute';
import Home from './pages/home';
import { Login, Logout } from './pages/login';
import Signup from './pages/signup';
import AddBlog from './pages/addblog';
import ViewBlog from './pages/Viewblog';
import AuthorProfile from './pages/author';
import "./App.css";avbar

function App() {
  return (
    <BrowserRouter>
      <AuthProvider> {/* Wrapping the app in AuthProvider to give all components access to authentication status */}
        <AppContent />
      </AuthProvider>
    </BrowserRouter>
  );
}

function AppContent() {
  const { user } = useAuth();  // Use the `useAuth` hook to access the `user` authentication state

  return (
    <>
      <nav className="navbar">
        <div className="nav-container">
          <h1 className="nav-logo">CW2Blog</h1>
          <ul className="nav-links">
            {user ? (
              <>
                <li><Link to="/add">Add Blog</Link></li>
                <li><Link to="/browse">Browse</Link></li>
                <li>
                  <Logout /> {/* Logout component */}
                </li>
              </>
            ) : (
              <>
                <li><Link to="/login">Login</Link></li>
                <li><Link to="/signup">Sign up</Link></li>
              </>
            )}
          </ul>
        </div>
      </nav>

      <Routes>
        {/* Redirect logged-in users away from the login and signup pages */}
        <Route path="/login" element={user ? <Navigate to="/browse" /> : <Login />} />
        <Route path="/signup" element={user ? <Navigate to="/browse" /> : <Signup />} />

        {/* Public Routes */}
        <Route path="/" element={<Home />} />

        {/* Protected Routes */}
        <Route element={<ProtectedRoute />}>
          <Route path="/add" element={<AddBlog />} />
          <Route path="/browse" element={<ViewBlog />} />
          <Route path="/authors/:authorId/:username" element={<AuthorProfile />} />
        </Route>

        {/* Redirect non-authenticated users to the login page if trying to access protected routes */}
        <Route path="*" element={user ? <Navigate to="/browse" /> : <Navigate to="/login" />} />
      </Routes>
    </>
  );
}

export default App;
