import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';  // Import AuthProvider
import ProtectedRoute from './components/ProtectedRoute';
import Home from './pages/home';
import { Login, Logout } from './pages/login';
import Signup from './pages/signup';
import AddBlog from './pages/addblog';
import ViewBlog from './pages/Viewblog';
import AuthorProfile from './pages/author';
import "./App.css"
import { Link } from 'react-router-dom';
import { useAuth } from './context/AuthContext';  // Import useAuth hook

function App() {
  return (
    <BrowserRouter>
      <AuthProvider> {/* Make sure AuthProvider is wrapping the whole app */}
        <AppContent />
      </AuthProvider>
    </BrowserRouter>
  );
}

function AppContent() {
  const { user } = useAuth();  // Access user from context

  return (
    <>
      {user && (
        <nav className="navbar">
          <div className="nav-container">
            <h1 className="nav-logo">CW2Blog</h1>
            <ul className="nav-links">
              <li><Link to="/add">Add Blog</Link></li>
              <li><Link to="/browse">Browse</Link></li>
              <li>
                <Logout /> {/* Logout component */}
              </li>
            </ul>
          </div>
        </nav>
      )}
      {!user && (
        <nav className="navbar">
          <div className="nav-container">
            <h1 className="nav-logo">CW2Blog</h1>
            <ul className="nav-links">
              <li><Link to="/login">Login</Link></li>
              <li><Link to="/signup">Sign up</Link></li>
            </ul>
          </div>
        </nav>
      )}
      <Routes>
        {!user && (
          <>
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
          </>
        )}
        <Route path="/" element={<Home />} />
        <Route element={<ProtectedRoute />}>
          <Route path="/add" element={<AddBlog />} />
          <Route path="/browse" element={<ViewBlog />} />
          <Route path="/authors/:authorId/:username" element={<AuthorProfile />} />
        </Route>
      </Routes>
    </>
  );
}

export default App;
