import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext'; 
import ProtectedRoute from './components/ProtectedRoute';
import Home from './pages/home';
import Login from './pages/login';
import Signup from './pages/signup';
import AddBlog from './pages/addblog';
import ViewBlog from './pages/Viewblog';
import AuthorProfile from './pages/author';
import "./App.css"
import { Link } from 'react-router-dom';
  const { login } = useAuth();

function App() {

  const isAuthenticated = localStorage.getItem('authToken');


  return (
    <BrowserRouter>
      <AuthProvider>
     {isAuthenticated && (
          <nav className="navbar">
            <div className="nav-container">
              <h1 className="nav-logo">CW2Blog</h1>
              <ul className="nav-links">
                <li><Link to="/add">Add Blog</Link></li>
                <li><Link to="/browse">Browse</Link></li>
                 <li>
          <button onClick={AuthProvider.logout}  className="logout-button">
            Logout
          </button>
        </li>
              </ul>
            </div>
          </nav>
        )}
            {!isAuthenticated && (
          <nav className="navbar">
            <div className="nav-container">
              <h1 className="nav-logo">CW2Blog</h1>
              <ul className="nav-links">
                <li><Link to="/login">Signup</Link></li>
                <li><Link to="/signup">Login</Link></li>
              </ul>
            </div>
          </nav>
        )}
        <Routes>
{!isAuthenticated && (
  <>
    <Route path="/login" element={<Login />} />
    <Route path="/signup" element={<Signup />} />
  </>
)}
          <Route path="/" element={<Home />} />


          <Route element={<ProtectedRoute />}>
            <Route path="/add" element={<AddBlog />} />
            <Route path="/browse" element={<ViewBlog />} />
            <Route path="/authors/:authorId/:username" element={<AuthorProfile />} />

          </Route>
        </Routes>
      </AuthProvider>
    </BrowserRouter>
  );
}

export default App;
