import { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { format } from 'date-fns';

const ViewBlog = () => {
  const { user } = useAuth();
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    postCount: 5,
    sortBy: 'recent',
    country: '',
    searchQuery: ''
  });

  const countries = ['USA', 'Canada', 'UK', 'Japan', 'Australia', 'France', 'Germany'];

  useEffect(() => {
    const fetchBlogs = async () => {
      let url = '';
      const { searchQuery, country, postCount, sortBy } = filters;

      if (searchQuery) {
        url = `/api/blogs/search?search=${searchQuery}&limit=${postCount}&sort=${sortBy}`;
      } else if (country) {
        url = `/api/blogs/country?country=${country}&limit=${postCount}&sort=${sortBy}`;
      } else {
        url = `/api/blogs/fetch?limit=${postCount}&sort=${sortBy}`;
      }

      try {
        setLoading(true);
        const res = await fetch(url);
        const data = await res.json();
        setBlogs(data);
      } catch (e) {
        console.error('Error loading blogs:', e);
      } finally {
        setLoading(false);
      }
    };

    fetchBlogs();
  }, [filters]);

  // Unified function for handling both likes and dislikes
  const handleReaction = async (blogId, reaction) => {
    if (!user) return;

    try {
      const response = await fetch(`http://localhost:5000/api/blogs/reaction?blogId=${blogId}&reaction=${reaction}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`,
        },
        body: JSON.stringify({
          email: user.email,
        }),
      });

      if (response.ok) {
        setBlogs(blogs.map(blog => {
          if (blog._id === blogId) {
            return {
              ...blog,
              // Update likes or dislikes depending on the reaction
              [reaction === 'like' ? 'likes' : 'dislikes']: blog[reaction === 'like' ? 'likes' : 'dislikes'] + 1,
              userReaction: reaction,
            };
          }
          return blog;
        }));
      }
    } catch (error) {
      console.error(`Error ${reaction} blog:`, error);
    }
  };

  return (
    <div className="blog-container">
      <div className="filter-section">
        <select
          value={filters.postCount}
          onChange={(e) => setFilters({...filters, postCount: e.target.value})}
        >
          {[3, 5, 10, 15, 20].map(num => (
            <option key={num} value={num}>Show {num} posts</option>
          ))}
        </select>

        <select
          value={filters.sortBy}
          onChange={(e) => setFilters({...filters, sortBy: e.target.value})}
        >
          <option value="recent">Most Recent</option>
          <option value="popular">Most Popular</option>
          <option value="most_viewed">Most Viewed</option>
        </select>

        <select
          value={filters.country}
          onChange={(e) => setFilters({...filters, country: e.target.value})}
        >
          <option value="">All Countries</option>
          {countries.map(country => (
            <option key={country} value={country}>{country}</option>
          ))}
        </select>

        <input
          type="text"
          placeholder="Search blogs..."
          value={filters.searchQuery}
          onChange={(e) => setFilters({...filters, searchQuery: e.target.value})}
        />
      </div>

      {loading ? (
        <p>Loading blogs...</p>
      ) : (
        <div className="blog-list">
          {blogs.map(blog => (
            <div key={blog._id} className="blog-card">
              <h3>{blog.title}</h3>
              <div className="blog-meta">
                <span>üìç {blog.country}</span>
                <span>üóì {format(new Date(blog.visit_date), 'MMMM yyyy')}</span>
              </div>
              <p>{blog.content.substring(0, 200)}...</p>

              <div className="reaction-buttons">
                <button
                  onClick={() => handleReaction(blog._id, 'like')}
                  className={blog.userReaction === 'like' ? 'active' : ''}
                >
                  üëç {blog.likes || 0}
                </button>
                <button
                  onClick={() => handleReaction(blog._id, 'dislike')}
                  className={blog.userReaction === 'dislike' ? 'active' : ''}
                >
                  üëé {blog.dislikes || 0}
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ViewBlog;
