import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext'; 
import ProtectedRoute from './components/ProtectedRoute';
import Home from './pages/home';
import Login from './pages/login';
import Signup from './pages/signup';
import AddBlog from './pages/addblog';
import ViewBlog from './pages/Viewblog';
import AuthorProfile from './pages/author';
import "./App.css";
import { Link } from 'react-router-dom';
import { useAuth } from './context/AuthContext';

function App() {
  const { logout, user } = useAuth();  // Get user directly from context

  return (
    <AuthProvider>
      <BrowserRouter>
        {/* Navigation Bar */}
        {user ? (
          <nav className="navbar">
            <div className="nav-container">
              <h1 className="nav-logo">CW2Blog</h1>
              <ul className="nav-links">
                <li><Link to="/add">Add Blog</Link></li>
                <li><Link to="/browse">Browse</Link></li>
                <li>
                  <button onClick={logout} className="logout-button">
                    Logout
                  </button>
                </li>
              </ul>
            </div>
          </nav>
        ) : (
          <nav className="navbar">
            <div className="nav-container">
              <h1 className="nav-logo">CW2Blog</h1>
              <ul className="nav-links">
                <li><Link to="/login">Login</Link></li>
                <li><Link to="/signup">Signup</Link></li>
              </ul>
            </div>
          </nav>
        )}

        {/* Routes */}
        <Routes>
          {/* Routes only accessible to unauthenticated users */}
          {!user && (
            <>
              <Route path="/login" element={<Login />} />
              <Route path="/signup" element={<Signup />} />
            </>
          )}

          <Route path="/" element={<Home />} />

          {/* Protected Routes */}
          <Route element={<ProtectedRoute />}>
            <Route path="/add" element={<AddBlog />} />
            <Route path="/browse" element={<ViewBlog />} />
            <Route path="/authors/:authorId/:username" element={<AuthorProfile />} />
          </Route>
        </Routes>
      </BrowserRouter>
    </AuthProvider>
  );
}

export default App;
