import { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { format } from 'date-fns';

const Viewblog = () => {
  const { user } = useAuth();
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    postCount: 5,
    sortBy: 'recent',
    country: '',
    searchQuery: ''
  });

  // Available countries for filter
  const countries = ['USA', 'Canada', 'UK', 'Japan', 'Australia', 'France', 'Germany'];

  // Fetch blogs when filters change
  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        setLoading(true);
        const query = new URLSearchParams({
          limit: filters.postCount,
          sort: filters.sortBy,
          country: filters.country,
          search: filters.searchQuery
        }).toString();

        const response = await fetch(`http://localhost:5000/api/blogs?${query}`);
        const data = await response.json();
        setBlogs(data);
      } catch (error) {
        console.error('Error fetching blogs:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchBlogs();
  }, [filters]);

  // Handle like/dislike
  const handleReaction = async (blogId, action) => {
    if (!user) return;

    try {
      const response = await fetch(`http://localhost:5000/api/blogs/${blogId}/${action}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        }
      });

      if (response.ok) {
        setBlogs(blogs.map(blog => {
          if (blog._id === blogId) {
            return {
              ...blog,
              likes: action === 'like' ? blog.likes + 1 : blog.likes,
              dislikes: action === 'dislike' ? blog.dislikes + 1 : blog.dislikes,
              userReaction: action
            };
          }
          return blog;
        }));
      }
    } catch (error) {
      console.error('Error reacting to blog:', error);
    }
  };

  return (
    <div className="blog-container">
      <div className="filter-section">
        <select 
          value={filters.postCount}
          onChange={(e) => setFilters({...filters, postCount: e.target.value})}
        >
          {[3, 5, 10, 15, 20].map(num => (
            <option key={num} value={num}>Show {num} posts</option>
          ))}
        </select>

        <select
          value={filters.sortBy}
          onChange={(e) => setFilters({...filters, sortBy: e.target.value})}
        >
          <option value="recent">Most Recent</option>
          <option value="popular">Most Popular</option>
        </select>

        <select
          value={filters.country}
          onChange={(e) => setFilters({...filters, country: e.target.value})}
        >
          <option value="">All Countries</option>
          {countries.map(country => (
            <option key={country} value={country}>{country}</option>
          ))}
        </select>

        <input
          type="text"
          placeholder="Search blogs..."
          value={filters.searchQuery}
          onChange={(e) => setFilters({...filters, searchQuery: e.target.value})}
        />
      </div>

      {loading ? (
        <p>Loading travel blogs...</p>
      ) : (
        <div className="blog-list">
          {blogs.map(blog => (
            <div key={blog._id} className="blog-card">
              <h3>{blog.title}</h3>
              <div className="blog-meta">
                <span>üìç {blog.country}</span>
                <span>üóì {format(new Date(blog.visit_date), 'MMMM yyyy')}</span>
              </div>
              <p>{blog.content.substring(0, 200)}...</p>
              
              <div className="reaction-buttons">
                <button 
                  onClick={() => handleReaction(blog._id, 'like')}
                  className={blog.userReaction === 'like' ? 'active' : ''}
                >
                  üëç {blog.likes || 0}
                </button>
                <button 
                  onClick={() => handleReaction(blog._id, 'dislike')}
                  className={blog.userReaction === 'dislike' ? 'active' : ''}
                >
                  üëé {blog.dislikes || 0}
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BlogList;
