import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';  // Import AuthProvider
import ProtectedRoute from './components/ProtectedRoute';
import Home from './pages/home';
import { Login, Logout } from './pages/login';
import Signup from './pages/signup';
import AddBlog from './pages/addblog';
import ViewBlog from './pages/Viewblog';
import AuthorProfile from './pages/author';
import "./App.css";
import { Link } from 'react-router-dom';
import { useAuth } from './context/AuthContext';  // Import useAuth hook

function App() {
  return (
    <BrowserRouter>
      <AuthProvider> {/* Make sure AuthProvider is wrapping the whole app */}
        <AppContent />
      </AuthProvider>
    </BrowserRouter>
  );
}

function AppContent() {
  const { user } = useAuth();  // Access user from context

  return (
    <>
      <nav className="navbar">
        <div className="nav-container">
          <h1 className="nav-logo">CW2Blog</h1>
          <ul className="nav-links">
            {user ? (
              <>
                <li><Link to="/add">Add Blog</Link></li>
                <li><Link to="/browse">Browse</Link></li>
                <li>
                  <Logout /> {/* Logout component */}
                </li>
              </>
            ) : (
              <>
                <li><Link to="/login">Login</Link></li>
                <li><Link to="/signup">Sign up</Link></li>
              </>
            )}
          </ul>
        </div>
      </nav>

      <Routes>
        {/* Public Routes for non-authenticated users */}
        {!user && (
          <>
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
          </>
        )}

        {/* Protected Routes for authenticated users */}
        <Route path="/" element={<Home />} />

        <Route element={<ProtectedRoute />}>
          <Route path="/add" element={<AddBlog />} />
          <Route path="/browse" element={<ViewBlog />} />
          <Route path="/authors/:authorId/:username" element={<AuthorProfile />} />
        </Route>

        {/* If the user tries to access a protected route without being logged in */}
        <Route path="*" element={<ProtectedRoute redirectTo="/login" />} />
      </Routes>
    </>
  );
}

export default App;
